var SS = SpreadsheetApp.openById("1cWsXPZZqC82z3gcMnCMF2AqKgrjNOKDZ1hmkilwniL8");

function doPost(e) {
  var parsedData;
  var result = {};

  try { 
    parsedData = JSON.parse(e.postData.contents);
  } catch(f){
    return ContentService.createTextOutput("Error in parsing request body: " + f.message);
  }

  if (parsedData !== undefined){
    var sheet = SS.getSheetByName(parsedData.sheet_name); // sheet name to publish data to is specified in Arduino code
    var dataArr = JSON.parse(parsedData.values); // creates an array of the values to publish

    // Default time zone is America/Chicago.
    var date_now = Utilities.formatDate(new Date(), "Asia/Jakarta", "yyyy/MM/dd"); // gets the current date
    var time_now = Utilities.formatDate(new Date(), "Asia/Jakarta", "HH:mm:ss"); // gets the current time

    var id_data = dataArr[0]; // value0 from Arduino code
    var voltage = dataArr[1]; // value1 from Arduino code
    var current = dataArr[2]; // value2 from Arduino code
    var power = dataArr[3]; // value3 from Arduino code
    var energy = dataArr[4]; // value4 from Arduino code
    var frequency = dataArr[5]; // value1 from Arduino code
    var pf = dataArr[6]; // value2 from Arduino code
    var va = dataArr[7]; // value3 from Arduino code
    var var1 = dataArr[8]; // value4 from Arduino code

    // Read and execute command from the "payload_base" string specified in Arduino code
    switch (parsedData.command) {
      case "append_row":
        var publish_array = [date_now, time_now, id_data, voltage, current, power, energy, frequency, pf, va, var1];
        sheet.appendRow(publish_array); // publish data in publish_array after the last row of data in the sheet

        str = "Success"; // string to return back to Arduino serial console
        SpreadsheetApp.flush();
        break;     
    }

    return ContentService.createTextOutput(str);
  } else {
    return ContentService.createTextOutput("Error! Request body empty or in incorrect format.");
  }
}

