function resetEnergy() {
  var sheet = SS.getSheetByName('kamar_1');

  // Menemukan baris terakhir yang berisi data dalam kolom G untuk setiap kamar
  var lastRowKamar1 = sheet.getRange('G:G').getValues().filter(String).length;

  // Menyimpan nilai energi total saat ini sebelum direset
  var currentEnergy = sheet.getRange('S3').getValue();

  // Menambahkan harga listrik sebelum pajak
  var electricityCost = currentEnergy * 1444;

  // Menghitung pajak
  var tax = electricityCost * 0.12;

  // Menghitung total harga (harga listrik ditambah pajak)
  var totalCost = electricityCost + tax;

  // Menambahkan simbol mata uang ("Rp") ke harga listrik, pajak, dan total harga
  var electricityCostFormatted = "Rp " + electricityCost.toLocaleString('id-ID');
  var taxFormatted = "Rp " + tax.toLocaleString('id-ID');
  var totalCostFormatted = "Rp " + totalCost.toLocaleString('id-ID');

  // Menetapkan rumus baru ke sel G2 untuk menghitung total energi dari baris setelah reset terakhir hingga baris terakhir untuk kamar
  var newStartRowKamar1 = lastRowKamar1 + 1;
  sheet.getRange('S3').setFormula('=SUM(G' + newStartRowKamar1 + ':G)');

  // Menambahkan entri baru ke sheet histori
  var historySheet = SS.getSheetByName('histori');
  if (!historySheet) {
    historySheet = SS.insertSheet('histori');
    historySheet.appendRow(["No", "Waktu", "Energi Total", "Harga Listrik", "Pajak", "Total Harga"]);
  }
  var timestamp = new Date();
  historySheet.appendRow([historySheet.getLastRow(), timestamp, currentEnergy, electricityCostFormatted, taxFormatted, totalCostFormatted]);

  // Mengembalikan pesan berhasil
  return "Reset energi kamar 1 berhasil";
}

function getSheetData() {
  var ss = SpreadsheetApp.getActiveSpreadsheet();
  var dataSheet = ss.getSheetByName('histori');
  var dataRange = dataSheet.getDataRange();
  var dataValues = dataRange.getValues();

  // Convert the timestamp to desired format and numbers to fixed decimal places
  for (var i = 1; i < dataValues.length; i++) {
    // Convert timestamp to desired format
    var timestamp = new Date(dataValues[i][1]);
    dataValues[i][1] = Utilities.formatDate(timestamp, "Asia/Jakarta", "dd/MM/yyyy HH:mm:ss");

    // Convert numbers to fixed decimal places
    for (var j = 2; j < dataValues[i].length; j++) {
      dataValues[i][j] = dataValues[i][j].toLocaleString('id-ID', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
    }
  }
  return dataValues;
}


